/**
 * Extracts a Confluence page ID from a URL
 * @param {string} inputNodeName - The name of the node containing the Confluence URL
 * @returns {Object} An object containing the extracted pageId or null if not found
 */
function extractPageId(inputNodeName = '[PARSE] - AI JSON Response') {
  try {
    // Get URL from the input node
    const url = $(inputNodeName).first().json.confluence_url;

    if (!url) {
      console.warn("No Confluence URL found in the input.");
      return { pageId: null, error: "No URL provided" };
    }

    // Try to extract pageId from URL parameters
    const urlParams = new URL(url).searchParams;
    if (urlParams.has('pageId')) {
      return validatePageId(urlParams.get('pageId'));
    }

    // Try to extract from friendly URL pattern
    const friendlyUrlMatch = url.match(/\/pages\/(\d+)/);
    if (friendlyUrlMatch && friendlyUrlMatch[1]) {
      return validatePageId(friendlyUrlMatch[1]);
    }

    console.warn("No valid Confluence page ID pattern found in URL:", url);
    return { pageId: null, error: "No valid page ID pattern found" };

  } catch (error) {
    console.error("Error extracting page ID:", error.message);
    return { 
      pageId: null, 
      error: error.message,
      stack: error.stack 
    };
  }
}

/**
 * Validates if the extracted ID is a valid Confluence page ID
 * @param {string} pageId - The page ID to validate
 * @returns {Object} An object with the validated pageId or null if invalid
 */
function validatePageId(pageId) {
  if (!pageId) {
    return { pageId: null, error: "No page ID provided" };
  }

  // Check if pageId contains only digits
  if (/^\d+$/.test(pageId)) {
    return { pageId, error: null };
  }

  console.error("Invalid page ID format. Expected numeric value, got:", pageId);
  return { pageId: null, error: "Invalid page ID format" };
}

// Main execution
const result = extractPageId();
return result;

